package Core_java_examples;

public class operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		a+=3;//a=a+3=10+3=13
		System.out.println(a);
		int b=10;
		b-=4;//
		System.out.println(b);
		a*=2;//13*2=26
		System.out.println(a);
		a/=2;//26/2=13
		System.out.println(a);
		
				
	}

}
